svg.script:
var intervalRef

var swimmingPath
var eyeLeft
var eyeRight

var aGroup
var maxOffset = 16
var step = 0


document.addEventListener("DOMContentLoaded", function(event) {
    swimmingPath = document.getElementById("path4138")
    aGroup = document.getElementById("path4421")

    document.getElementById("svg2").addEventListener("click", function () {
        if (intervalRef) {
            window.clearInterval(intervalRef)
            intervalRef = null
        } else {
            letsSwim()
        }
    })
})

function letsSwim() {
    intervalRef = setInterval(function () {
        var delta = getDelta(step)
        var xDelta = delta/8
        var yDelta = delta/2
        var aPath = buildWavePath(step, delta, xDelta, yDelta)
        swimmingPath.setAttribute("d", aPath)
        aGroup.setAttribute("transform", "translate(" + 0 + "," + yDelta + ")")
        step += 1
    }, 25)
}

function getDelta(index) {
    var p = index % (maxOffset*2)
    var rvalue
    if (p>maxOffset) {
        rvalue = maxOffset-(p%maxOffset)
    } else {
        rvalue = p
    }
    rvalue /= 2
    return rvalue-(maxOffset/3)
}

function buildWavePath(index, delta, xDelta, yDelta) {
    var path = ""

    path += " M " + (0+xDelta) + "," + (234.85628+yDelta)
    path += " L " + (0+xDelta) + "," + (49.437012+yDelta)
    path += " C " + (0+xDelta) + "," + (29.658958+yDelta) + " " + (21.190773+xDelta) + "," + (11.99998+yDelta) + " " + (54.036471+xDelta) + "," + (11.99998+yDelta)
    path += " C " + (116.54925+xDelta) +"," + (11.99998+yDelta) + " " + (141.625+xDelta) + "," + (26.127162+yDelta) + " " + (141.625+xDelta) + "," + (53.675167+yDelta)

    path += " L " + (141.625+xDelta) + "," + (234.85628+yDelta)

    path += " C " + (127.16358-delta/2.00+xDelta) + "," + (248.48715+delta*3/8+yDelta) + " " + (105.21688-delta/1.50+xDelta) + "," + (236.128+delta/2.25+yDelta)  + " " + (94.795598-delta/2.00+xDelta) + "," + (224.21674+delta/2.25+yDelta)
    path += " C " + (84.374312-delta/2.00+xDelta) + "," + (212.30548-delta/8+yDelta)   + " " + (62.104711-delta/1.50+xDelta) + "," + (219.37721-delta/8+yDelta)   + " " + (52.435824-delta/2.00+xDelta) + "," + (225.06658-delta/8+yDelta)

    path += " C " + (42.766937+xDelta) + "," + (230.75595+yDelta) + " " + (0+xDelta) + "," + (244.95536+yDelta) + "   " + (0+xDelta) + "," + (234.85628+delta/2+yDelta)

    path += " Z"

    return path
}
